function content(){
	
}

//列转化成JSON数据
content.prototype.tCol=function (coldata){
	var result = {};
	for(var i=0;i<coldata.length;i++){
		result[coldata[i].COLUMN_NAME]='';
	}
	//返回一个带有表所有字段的JSON数组值
	return result;
}

//列值转换成数组
content.prototype.tArr=function(coldata){
	var result = new Array(coldata.length);
	for(var i=0;i<coldata.length;i++){
		result[i] =coldata[i].COLUMN_NAME;
	}
	return result;
}

//数据赋值
//dddate--->目标字段
//ydate--->源字段
//所有的列名字
content.prototype.initD=function(ddate,ydate,colname){
	for(var i=0;i<colname.length;i++){
		ddate[colname[i]]=ydate[colname[i]];
	}
	return ddate;
}
content.prototype._COLSQL='select COLUMN_NAME,TABLE_SCHEMA from information_schema.COLUMNS where table_name=? and table_schema="project"';
content.prototype._DATABASE='project';
module.exports=new content();